// Constructor
function FindPathInfo(type, firstElm, dest, elementList, numNodes) {


    this.dest = dest;
    this.type = type;
    this.firstElm = firstElm;
    this.elmList = elementList;

    this.used = new Array(numNodes);
    //zeroArray(this.used);
}
