/** initializes the values of scalefactors for performance reasons */
CirSim.initScaleFactors = function () {
    var numScaleFactors = 200;
    for (var i = 0; i < numScaleFactors; ++i) {
        CirSim.scaleFactors[i] = 0;
    }
};

/** Input array a is a two dimensional array */
CirSim.lu_factor = function (a, n, ipvt) {

    var i = 0;
    var j = 0;
    var k = 0;

    // Divide each row by largest element in that row and remember scale factors
    for (i = 0; i < n; ++i) {
        var largest = 0;

        for (j = 0; j < n; ++j) {
            var x = Math.abs(a[i][j]);
            if (x > largest)
                largest = x;
        }
        // Check for singular matrix:
        if (largest == 0)
            return false;
        CirSim.scaleFactors[i] = 1.0 / largest;
    }

    // Crout's method: Loop through columns first
    for (j = 0; j < n; ++j) {

        // Calculate upper trangular elements for this column:
        for (i = 0; i < j; ++i) {
            var q = a[i][j];

            for (k = 0; k != i; ++k)
                q -= a[i][k] * a[k][j];

            a[i][j] = q;
        }

        // Calculate lower triangular elements for this column
        var largest = 0;
        var largestRow = -1;

        for (i = j; i < n; ++i) {
            var q = a[i][j];

            for (k = 0; k < j; ++k)
                q -= a[i][k] * a[k][j];

            a[i][j] = q;
            var x = Math.abs(q);

            if (x >= largest) {
                largest = x;
                largestRow = i;
            }
        }

        // Pivot
        if (j != largestRow) {
            var x;

            for (var k = 0; k < n; ++k) {
                x = a[largestRow][k];
                a[largestRow][k] = a[j][k];
                a[j][k] = x;
            }
            CirSim.scaleFactors[largestRow] = CirSim.scaleFactors[j];
        }

        // keep track of row interchanges
        ipvt[j] = largestRow;

        // avoid zeros
        if (a[j][j] == 0) {
            //console.log("avoided zero");
            a[j][j] = 1e-18;
        }

        if (j != n - 1) {
            var mult = 1 / a[j][j];
            for (i = j + 1; i != n; ++i)
                a[i][j] *= mult;
        }

    }

    return true;
};

/** Solves the factored matrix */
CirSim.lu_solve = function (a, n, ipvt, b) {
    var i;

    // find first nonzero b element
    for (i = 0; i < n; ++i) {
        var row = ipvt[i];

        var swap = b[row];
        b[row] = b[i];
        b[i] = swap;
        if (swap != 0)
            break;
    }

    var bi = i++;
    for (; i < n; ++i) {
        var row = ipvt[i];
        var j;
        var tot = b[row];

        b[row] = b[i];
        // Forward substitution by using the lower triangular matrix;
        for (j = bi; j < i; ++j)
            tot -= a[i][j] * b[j];
        b[i] = tot;

    }

    for (i = n - 1; i >= 0; i--) {
        var tot = b[i];

        // back-substitution using the upper triangular matrix
        var j;
        for (j = i + 1; j != n; ++j)
            tot -= a[i][j] * b[j];
        b[i] = tot / a[i][i];

    }
};

