    if (this.waveform == VoltageElm.WF_DC)
        return null;
    if (n == 2)
        return new EditInfo("Frequency (Hz)", this.frequency, 4, 500);
    if (n == 3)
        return new EditInfo("DC Offset (V)", this.bias, -20, 20);
